<?php

use modules\sales\common\models\Sale;

/** @var Sale $sale */
$documents = $sale->documents;
?>

<?php if (!empty($documents)): ?>
    <?php foreach ($documents as $document): ?>
        <div>
            <?php if ($document->isPdf): ?>
                <a href="<?= $document->url ?>" class="btn btn-default fancybox-pdf" target="_blank"
					title="<?= $document->url ?>">
					<img style="height:60px"
						src="data:image/png;base64,"/>
				</a>
            <?php else: ?>
                <a href="<?= $document->url ?>" class="btn btn-default fancybox" target="_blank"
				   title="<?= $document->url ?>">
                    <img src="<?= $document->url ?>" style="max-width:150px; max-height:150px;"/>
                </a>
            <?php endif; ?>
        </div>
    <?php endforeach ?>
<?php endif; ?>